import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import styled from "styled-components";

const ModalContent = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const Input = styled.input`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  width: 100%;
`;

const Button = styled.button`
  background-color: #333;
  color: white;
  padding: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  width: 100%;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 2px;
  right: 2px;
  background: none;
  border: 1px solid #333;
  color: #333;
  font-size: 26px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;

  &:hover {
    background-color: black;
    color: white;
  }
`;

const StatusCell = styled.td`
  border: 1px solid #ddd;
  text-align: center; /* Center the text */
`;

const StatusText = styled.span`
  background-color: ${({ status }) => getStatusBackgroundColor(status)};
  color: white; /* Text color for contrast */
  border-radius: 5px; /* Rounded corners */
  padding: 5px 10px; /* Add padding around the text */
  display: inline-block; /* Allow the background to wrap only around the text */
`;

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [sortColumn, setSortColumn] = useState('companyName');
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:8080/api/job-applications", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setJobApplications(response.data);
    };

    fetchData();
  }, []);

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const handleRowClick = (application) => {
    setSelectedApplication(application);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedApplication(null);
  };

  const handleDelete = async (id, e) => {
    e.stopPropagation();
    const token = localStorage.getItem("token");
    await axios.delete(`http://localhost:8080/api/job-applications/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setJobApplications(prev => prev.filter(app => app.id !== id));
  };

  const handleSaveChanges = async () => {
    const token = localStorage.getItem("token");
    await axios.put(`http://localhost:8080/api/job-applications/${selectedApplication.id}`, selectedApplication, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setJobApplications(prev =>
        prev.map(app => app.id === selectedApplication.id ? selectedApplication : app)
    );
    closeModal();
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedApplication(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const sortedApplications = [...jobApplications].sort((a, b) => {
    if (a[sortColumn] < b[sortColumn]) return sortOrder === 'asc' ? -1 : 1;
    if (a[sortColumn] > b[sortColumn]) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const filteredApplications = sortedApplications.filter(app =>
      Object.values(app).some(value =>
          value.toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
  );

  return (
      <div style={styles.container}>
        <h2 style={styles.header}>Job Applications</h2>
        <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={styles.searchInput}
        />
        <table style={styles.table}>
          <thead>
          <tr>
            {['Company', 'Position', 'Status', 'Application Date', 'Response Date', 'Platform'].map((column) => (
                <th key={column} onClick={() => handleSort(column)} style={styles.tableHeader}>
                  {column.charAt(0).toUpperCase() + column.slice(1)}
                  {sortColumn === column && (
                      <span className="material-icons">
                    {sortOrder === 'asc' ? 'arrow_upward' : 'arrow_downward'}
                  </span>
                  )}
                </th>
            ))}
            <th> </th>
          </tr>
          </thead>
          <tbody>
          {filteredApplications.map(app => (
              <tr key={app.id} style={styles.tableRow} onClick={() => handleRowClick(app)}>
                <td style={styles.tableCell}>{app.companyName}</td>
                <td style={styles.tableCell}>{app.jobTitle}</td>
                <StatusCell>
                  <StatusText status={app.status}>
                    {app.status}
                  </StatusText>
                </StatusCell>
                <td style={styles.tableCell}>{new Date(app.applicationDate).toLocaleDateString()}</td>
                <td style={styles.tableCell}>{new Date(app.responseDate).toLocaleDateString()}</td>
                <td style={styles.tableCell}>{app.platform}</td>
                <td style={styles.tableCell}>
                <span
                    className="material-icons"
                    style={styles.deleteIcon}
                    onClick={(e) => handleDelete(app.id, e)}
                >
                  delete
                </span>
                </td>
              </tr>
          ))}
          </tbody>
        </table>

        {/* Edit Modal */}
        {isModalOpen && selectedApplication && (
            <Modal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                style={{
                  overlay: {
                    backgroundColor: 'rgba(0, 0, 0, 0.7)',
                  },
                  content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    width: '400px',
                    padding: '20px',
                    borderRadius: '10px',
                    backgroundColor: '#f5f5f5',
                    color: '#333',
                  },
                }}
            >
              <ModalContent>
                <CloseButton onClick={closeModal}>&times;</CloseButton>
                <h2>Edit Job Application</h2>
                <label>
                  Company Name:
                  <Input
                      type="text"
                      name="companyName"
                      value={selectedApplication.companyName}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Job Title:
                  <Input
                      type="text"
                      name="jobTitle"
                      value={selectedApplication.jobTitle}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Status:
                  <Input
                      type="text"
                      name="status"
                      value={selectedApplication.status}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Application Date:
                  <Input
                      type="date"
                      name="applicationDate"
                      value={selectedApplication.applicationDate}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Response Date:
                  <Input
                      type="date"
                      name="responseDate"
                      value={selectedApplication.responseDate}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Platform:
                  <Input
                      type="text"
                      name="platform"
                      value={selectedApplication.platform}
                      onChange={handleInputChange}
                  />
                </label>
                <Button onClick={handleSaveChanges}>Save Changes</Button>
              </ModalContent>
            </Modal>
        )}
      </div>
  );
};

// Function to get background color based on status
const getStatusBackgroundColor = (status) => {
  switch (status.toLowerCase()) {
    case 'applied':
      return 'blue';
    case 'interview':
      return 'orange';
    case 'offer':
      return 'green';
    case 'rejected':
      return 'red';
    default:
      return 'gray'; // Default color if no match
  }
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  },
  header: {
    textAlign: 'center',
  },
  searchInput: {
    marginBottom: '20px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
  },
  table: {
    width: '200%',
    borderCollapse: 'collapse',
  },
  tableHeader: {
    backgroundColor: '#f0f0f0',
    padding: '10px',
    textAlign: 'left',
    cursor: 'pointer',
  },
  tableCell: {
    padding: '10px',
    border: '1px solid #ddd',
  },
  tableRow: {
    cursor: 'pointer',
    '&:hover': {
      backgroundColor: '#f9f9f9',
    },
  },
  deleteIcon: {
    cursor: 'pointer',
    color: 'red',
  },
};

export default JobApplications;
