import React, { useState } from 'react';
import styled from 'styled-components';
import AddJobApplication from './AddJobApplication';

const SidebarContainer = styled.div`
  width: ${(props) => (props.isExpanded ? '300px' : '60px')}; /* Width changes based on expansion */
  height: 100vh;
  background-color: #f5f5f5;
  padding: 20px;
  color: black;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border-radius: 0 0.675rem 0.675rem 0;
  transition: width 0.3s; /* Smooth transition for width change */
`;

const SidebarItem = styled.div`
  margin: 20px 0;
  cursor: pointer;
  padding: 10px;
  border-radius: 5px;
  display: flex;
  align-items: center;

  &:hover {
    background-color: #f2f3f4;
  }

  & > .material-icons {
    margin-right: ${(props) => (props.isExpanded ? '10px' : '0')}; /* Space between icon and text */
  }

  & > span {
    display: ${(props) => (props.isExpanded ? 'inline' : 'none')}; /* Show text only when expanded */
  }
`;



const ProfileContainer = styled.div`
    margin-top: auto; /* Push to bottom */
    text-align: center;
    color: #aaa; /* Light text color */
`;

const ProfileButton = styled.button`
    background: none;
    border: none;
    color: #aaa;
    cursor: pointer;
    padding: 10px;
    border-radius: 5px;

    &:hover {
        color: black;
    }
`;

const Sidebar = ({ setPage }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isExpanded, setIsExpanded] = useState(true); // State to control sidebar expansion


    const handleAddJobClick = () => {
        setIsModalOpen(true);
    };

    const toggleSidebar = () => {
    setIsExpanded((prev) => !prev); // Toggle the expanded state
  };


  return (
    <SidebarContainer isExpanded={isExpanded}>
      <div>
        <SidebarItem isExpanded={isExpanded} onClick={() => setPage('home')}>
          <span className="material-icons">home</span>
          <span>Home</span>
        </SidebarItem>
        <SidebarItem isExpanded={isExpanded} onClick={() => setPage('jobApplications')}>
          <span className="material-icons">work</span>
          <span>Applications</span>
        </SidebarItem>
        <SidebarItem isExpanded={isExpanded} onClick={handleAddJobClick}>
          <span className="material-icons">add</span>
          <span>Add Job Application</span>
        </SidebarItem>
      </div>
      <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />

      <ProfileContainer>
        <ProfileButton onClick={() => setPage('profile')}>
          <span className="material-icons">person</span>
          <span>Profile</span>
        </ProfileButton>
        <div>Your light text here</div>
      </ProfileContainer>

      {/* Collapse Button */}
      <CollapseButton onClick={toggleSidebar}>
        <span className="material-icons">{isExpanded ? 'chevron_left' : 'chevron_right'}</span>
      </CollapseButton>
    </SidebarContainer>
  );
};

export default Sidebar;
