import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

const JobApplications = () => {
  const [applications, setApplications] = useState([]);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [expandedComments, setExpandedComments] = useState(null); // Track which applicationâ€™s comments are expanded
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Fetch job applications from API
    axios.get('/api/job-applications')
        .then((response) => {
          setApplications(response.data);
        })
        .catch((error) => {
          console.error('Error fetching job applications:', error);
        });
  }, []);

  const handleEditApplication = (application) => {
    setSelectedApplication(application);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedApplication(null);
  };

  const handleSaveChanges = () => {
    // Update job application logic here
    closeModal();
  };

  const handleToggleComments = (applicationId) => {
    // Toggle comment visibility for a specific application
    setExpandedComments(expandedComments === applicationId ? null : applicationId);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSelectedApplication((prevApplication) => ({
      ...prevApplication,
      [name]: value,
    }));
  };

  const filteredApplications = applications.filter((app) =>
      app.companyName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      app.jobTitle.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
      <div style={styles.container}>
        <h1 style={styles.header}>Job Applications</h1>
        <input
            type="text"
            placeholder="Search by company or position"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={styles.searchInput}
        />
        <table style={styles.table}>
          <thead>
          <tr>
            <th style={styles.tableHeader}>Company</th>
            <th style={styles.tableHeader}>Position</th>
            <th style={styles.tableHeader}>Status</th>
            <th style={styles.tableHeader}>Application Date</th>
            <th style={styles.tableHeader}>Response Date</th>
            <th style={styles.tableHeader}>Platform</th>
            <th style={styles.tableHeader}>Job URL</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
          </thead>
          <tbody>
          {filteredApplications.map((app) => (
              <React.Fragment key={app.id}>
                <tr style={styles.tableRow}>
                  <td style={styles.tableCell}>{app.companyName}</td>
                  <td style={styles.tableCell}>{app.jobTitle}</td>
                  <td style={styles.tableCell}>{app.status}</td>
                  <td style={styles.tableCell}>{new Date(app.applicationDate).toLocaleDateString()}</td>
                  <td style={styles.tableCell}>{new Date(app.responseDate).toLocaleDateString()}</td>
                  <td style={styles.tableCell}>{app.platform}</td>
                  <td style={styles.tableCell}>
                    <a href={app.jobUrl} target="_blank" rel="noopener noreferrer">
                      View Job
                    </a>
                  </td>
                  <td style={styles.tableCell}>
                    <button onClick={() => handleEditApplication(app)}>Edit</button>
                    <button
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent triggering the edit modal
                          handleToggleComments(app.id);
                        }}
                    >
                      {expandedComments === app.id ? 'Hide Comment' : 'Show Comment'}
                    </button>
                  </td>
                </tr>
                {expandedComments === app.id && (
                    <tr style={styles.commentRow}>
                      <td colSpan="8" style={styles.tableCell}>
                        <strong>Comment:</strong> {app.comments ? app.comments : 'No comment'}
                      </td>
                    </tr>
                )}
              </React.Fragment>
          ))}
          </tbody>
        </table>

        {/* Edit Modal */}
        {isModalOpen && selectedApplication && (
            <Modal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                contentLabel="Edit Job Application"
                style={{
                  content: {
                    width: '50%',
                    height: 'auto',
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                  },
                }}
            >
              <CloseButton onClick={closeModal}>Ã—</CloseButton>
              <ModalContent>
                <h2>Edit Job Application</h2>
                <label>
                  Company Name:
                  <Input
                      type="text"
                      name="companyName"
                      value={selectedApplication.companyName}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Position:
                  <Input
                      type="text"
                      name="jobTitle"
                      value={selectedApplication.jobTitle}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Status:
                  <Input
                      type="text"
                      name="status"
                      value={selectedApplication.status}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Application Date:
                  <Input
                      type="date"
                      name="applicationDate"
                      value={new Date(selectedApplication.applicationDate).toISOString().split('T')[0]}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Response Date:
                  <Input
                      type="date"
                      name="responseDate"
                      value={new Date(selectedApplication.responseDate).toISOString().split('T')[0]}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Platform:
                  <Input
                      type="text"
                      name="platform"
                      value={selectedApplication.platform}
                      onChange={handleInputChange}
                  />
                </label>
                <label>
                  Job URL:
                  <Input
                      type="url"
                      name="jobUrl"
                      value={selectedApplication.jobUrl}
                      onChange={handleInputChange}
                  />
                </label>
                <Button onClick={handleSaveChanges}>Save Changes</Button>
              </ModalContent>
            </Modal>
        )}
      </div>
  );
};

const styles = {
  container: {
    margin: '20px',
  },
  header: {
    textAlign: 'center',
  },
  searchInput: {
    padding: '10px',
    marginBottom: '20px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  tableHeader: {
    cursor: 'pointer',
    backgroundColor: '#333',
    color: '#fff',
    padding: '10px',
  },
  tableRow: {
    cursor: 'pointer',
    '&:hover': {
      backgroundColor: '#f1f1f1',
    },
  },
  tableCell: {
    padding: '10px',
    border: '1px solid #ddd',
  },
  deleteIcon: {
    color: 'red',
    cursor: 'pointer',
  },
  commentRow: {
    backgroundColor: '#f9f9f9',
  },
};

export default JobApplications;
