import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);


  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const token = localStorage.getItem("token");
    const response = await axios.get("http://localhost:8080/api/job-applications", {
      headers: { Authorization: `Bearer ${token}` }
    });
    setJobApplications(response.data);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <h2>Job Applications</h2>
      <button onClick={openModal}>Add Job Application</button>
      <div style={{ padding: '20px' }}>
        <ul>
          {jobApplications.map(app => (
            <li key={app.id}>{app.companyName} - {app.jobTitle}</li>
          ))}
        </ul>
      </div>

      <AddJobApplication isOpen={isModalOpen} onClose={closeModal} refreshApplications={fetchData} />
    </div>
  );
};

export default JobApplications;
