import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const REACT_APP_BACKEND_URL = process.env.REACT_APP_BACKEND_URL+'/api';

const ModalContent = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative; /* for absolute positioning of the close button */
`;

const Input = styled.input`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  width: 100%;
`;

const TextArea = styled.textarea`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  width: 100%;
`;

const Button = styled.button`
  background-color: #333;
  color: white;
  padding: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  width: 100%;
`;

const FormRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;
  margin-bottom: 10px;
`;

const FormGroup = styled.div`
  flex: 1; /* Take equal space */
  margin: 5px;
  min-width: 180px;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 2px;
  right: 2px;
  background: none;
  border: 1px solid #333;
  color: #333;
  font-size: 26px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;

  &:hover {
    background-color: black;
    color: white;
  }
`;

const AddJobApplication = ({ isOpen, onClose }) => {
  const [companyName, setCompanyName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [status, setStatus] = useState('');
  const [applicationDate, setApplicationDate] = useState('');
  const [responseDate, setResponseDate] = useState('');
  const [platform, setPlatform] = useState('');
  const [jobUrl, setJobUrl] = useState('');
  const [description, setDescription] = useState('');
  const [comments, setComments] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Reset error message

    const jobApplication = {
      companyName,
      jobTitle,
      status,
      applicationDate,
      responseDate,
      platform,
      jobUrl,
      description,
      comments,
    };

    try {
      // Get JWT token from localStorage
      const token = localStorage.getItem('token');
      const response = await axios.post(`${REACT_APP_BACKEND_URL}/job-applications`, jobApplication, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 201) {
        navigate('/job-applications'); // Redirect to job applications page on success
      }

    } catch (error) {
      console.error('Error adding job application:', error);
      setErrorMessage('Failed to add job application. Please try again.');
    }
  };

  return (
      <Modal
          isOpen={isOpen}
          onRequestClose={onClose}
          style={{
            overlay: {
              backgroundColor: 'rgba(0, 0, 0, 0.7)',
            },
            content: {
              top: '50%',
              left: '50%',
              right: 'auto',
              bottom: 'auto',
              marginRight: '-50%',
              transform: 'translate(-50%, -50%)',
              width: '400px',
              padding: '20px',
              borderRadius: '10px',
              backgroundColor: '#f5f5f5',
              color: '#333',
            },
          }}
      >
        <ModalContent>
          <CloseButton onClick={onClose}>&times;</CloseButton>
          <h2>Add Job Application</h2>
          <FormRow>
            <FormGroup>
              <Input
                  type="text"
                  placeholder="Company Name"
                  value={companyName}
                  onChange={(e) => setCompanyName(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Input
                  type="text"
                  placeholder="Job Title"
                  value={jobTitle}
                  onChange={(e) => setJobTitle(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Input
                  type="text"
                  placeholder="Status"
                  value={status}
                  onChange={(e) => setStatus(e.target.value)}
              />
            </FormGroup>
          </FormRow>
          <FormRow>
            <FormGroup>
              <Input
                  type="date"
                  placeholder="Application Date"
                  value={applicationDate}
                  onChange={(e) => setApplicationDate(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Input
                  type="date"
                  placeholder="Response Date"
                  value={responseDate}
                  onChange={(e) => setResponseDate(e.target.value)}
              />
            </FormGroup>
          </FormRow>
          <FormRow>
            <FormGroup>
              <Input
                  type="text"
                  placeholder="Platform"
                  value={platform}
                  onChange={(e) => setPlatform(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Input
                  type="url"
                  placeholder="Job URL"
                  value={jobUrl}
                  onChange={(e) => setJobUrl(e.target.value)}
              />
            </FormGroup>
          </FormRow>
          <TextArea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="4"
          />
          <TextArea
              placeholder="Comments"
              value={comments}
              onChange={(e) => setComments(e.target.value)}
              rows="4"
          />
          <Button onClick={handleSubmit}>Submit</Button>
        </ModalContent>
      </Modal>
  );
};

export default AddJobApplication;
