import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AddJobApplication from './AddJobApplication';
import { AuthContext } from '../contexts/AuthContext';
import atsfsIcon from '../assets/atsfs.png';
import '../styles/Sidebar.css'; // Import the CSS file

const Sidebar = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { isLoggedIn, setIsLoggedIn } = useContext(AuthContext); // Use global isLoggedIn
    const navigate = useNavigate(); // Use navigate for routing

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token); // Update the state based on token presence
    }, []);

    const handleAddJobClick = () => {
        setIsModalOpen(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Remove token from localStorage
        setIsLoggedIn(false);
        navigate('/login'); // Navigate to login after logout
    };

    return (
        <div className="sidebar-container">
            <div>
                <div className="app-name">
                    <img src={atsfsIcon} alt="Icon" className="sidebar-icon" />
                    ATSFS
                </div>
                <div className="sidebar-item" onClick={() => navigate('/')}>
                    <span className="material-icons">home</span>
                    Home
                </div>

                {/* Profile Section */}
                <div className="profile-container">
                    {isLoggedIn ? (
                        <>
                            <hr className="horizontal-line" />
                            <div className="sidebar-item" onClick={() => navigate('/job-applications')}>
                                <span className="material-icons">work</span>
                                Applications
                            </div>
                            <div className="sidebar-item" onClick={handleAddJobClick}>
                                <span className="material-icons">add</span>
                                Add Job Application
                            </div>
                            <div className="sidebar-item" onClick={() => navigate('/charts')}>
                                <span className="material-icons">bar_chart</span>
                                Charts
                            </div>
                            <div className="sidebar-item" onClick={() => navigate('/ai-tools')}>
                                <span className="material-icons">build</span>
                                AI Tools
                            </div>
                            <div className="sidebar-item" onClick={() => navigate('/resume')}>
                                <span className="material-icons">assignment_ind</span>
                                My Resume
                            </div>
                            <div className="sidebar-item" onClick={() => navigate('/exportData')}>
                                <span className="material-icons">file_download</span>
                                Export My Data
                            </div>
                        </>
                    ) : (
                        <p></p>
                    )}
                </div>
            </div>

            <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />

            {/* Bottom Profile Section */}
            <div className="profile-container">
                <hr className="horizontal-line" />
                {isLoggedIn && <div>Logged in as User</div>}
                <div className="sidebar-item" onClick={isLoggedIn ? handleLogout : () => navigate('/login')}>
                    <span className="material-icons">{isLoggedIn ? 'logout' : 'login'}</span>
                    {isLoggedIn ? 'Logout' : 'Login'}
                </div>
            </div>
        </div>
    );
};

export default Sidebar;
