import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [sortColumn, setSortColumn] = useState('companyName');
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:8080/api/job-applications", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setJobApplications(response.data);
    };

    fetchData();
  }, []);

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const sortedApplications = [...jobApplications].sort((a, b) => {
    if (a[sortColumn] < b[sortColumn]) return sortOrder === 'asc' ? -1 : 1;
    if (a[sortColumn] > b[sortColumn]) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const filteredApplications = sortedApplications.filter(app =>
    Object.values(app).some(value =>
      value.toString().toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  const handleRowClick = (app) => {
    setSelectedApplication(app);
    setIsModalOpen(true);
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");
    await axios.delete(`http://localhost:8080/api/job-applications/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setJobApplications(jobApplications.filter(app => app.id !== id));
  };

  const handleUpdate = async () => {
    const token = localStorage.getItem("token");
    await axios.put(`http://localhost:8080/api/job-applications/${selectedApplication.id}`, selectedApplication, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setJobApplications(jobApplications.map(app => app.id === selectedApplication.id ? selectedApplication : app));
    setIsModalOpen(false);
  };

  const handleInputChange = (e) => {
    setSelectedApplication({
      ...selectedApplication,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Job Applications</h2>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={styles.searchInput}
      />
      <table style={styles.table}>
        <thead>
          <tr>
            {['Company', 'Title', 'Status', 'Application Date', 'Response Date', 'Platform', 'Actions'].map((column) => (
              <th key={column} onClick={() => handleSort(column)} style={styles.tableHeader}>
                {column.charAt(0).toUpperCase() + column.slice(1)}
                {sortColumn === column && (
                  <span className="material-icons">
                    {sortOrder === 'asc' ? 'arrow_upward' : 'arrow_downward'}
                  </span>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredApplications.map(app => (
            <tr key={app.id} style={styles.tableRow} onClick={() => handleRowClick(app)}>
              <td style={styles.tableCell}>{app.companyName}</td>
              <td style={styles.tableCell}>{app.jobTitle}</td>
              <td style={{ ...styles.tableCell, color: getStatusColor(app.status) }}>{app.status}</td>
              <td style={styles.tableCell}>{new Date(app.applicationDate).toLocaleDateString()}</td>
              <td style={styles.tableCell}>{new Date(app.responseDate).toLocaleDateString()}</td>
              <td style={styles.tableCell}>{app.platform}</td>
              <td style={styles.tableCell}>
                <button onClick={() => handleDelete(app.id)} style={styles.deleteButton}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedApplication && (
        <div style={styles.modal}>
          <div style={styles.modalContent}>
            <h2>Edit Job Application</h2>
            <form>
              <label>
                Company Name:
                <input type="text" name="companyName" value={selectedApplication.companyName} onChange={handleInputChange} />
              </label>
              <label>
                Job Title:
                <input type="text" name="jobTitle" value={selectedApplication.jobTitle} onChange={handleInputChange} />
              </label>
              <label>
                Status:
                <input type="text" name="status" value={selectedApplication.status} onChange={handleInputChange} />
              </label>
              <label>
                Application Date:
                <input type="date" name="applicationDate" value={selectedApplication.applicationDate} onChange={handleInputChange} />
              </label>
              <label>
                Response Date:
                <input type="date" name="responseDate" value={selectedApplication.responseDate} onChange={handleInputChange} />
              </label>
              <label>
                Platform:
                <input type="text" name="platform" value={selectedApplication.platform} onChange={handleInputChange} />
              </label>
              <button type="button" onClick={handleUpdate} style={styles.saveButton}>Save</button>
              <button type="button" onClick={() => setIsModalOpen(false)} style={styles.closeButton}>Close</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const getStatusColor = (status) => {
  switch (status) {
    case 'Applied':
      return 'blue';
    case 'Interview':
      return 'orange';
    case 'Rejected':
      return 'red';
    case 'Accepted':
      return 'green';
    default:
      return 'black';
  }
};

// CSS-in-JS styles
const styles = {
  container: {
    padding: '20px',
  },
  header: {
    marginBottom: '20px',
  },
  searchInput: {
    padding: '10px',
    marginBottom: '20px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    width: '100%',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  tableHeader: {
    padding: '10px',
    textAlign: 'left',
    border: '1px solid #ddd',
    cursor: 'pointer',
    backgroundColor: '#f2f2f2',
  },
  tableRow: {
    '&:nth-child(even)': {
      backgroundColor: '#f9f9f9',
    },
    '&:hover': {
      backgroundColor: '#f1f1f1',
    },
  },
  tableCell: {
    padding: '10px',
    border: '1px solid #ddd',
  },
  deleteButton: {
    backgroundColor: 'red',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    cursor: 'pointer',
  },
  modal: {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: 'white',
    padding: '20px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    zIndex: 1000,
  },
  modalContent: {
    display: 'flex',
    flexDirection: 'column',
  },
  saveButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
    marginTop: '10px',
  },
  closeButton: {
    backgroundColor: 'gray',
    color: 'white',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
    marginTop: '10px',
  },
};

export default JobApplications;
