const getStatusBackgroundColor = (status) => {
  switch (status) {
    case 'Applied':
      return '#ff9800'; // orange
    case 'Interview':
      return '#4caf50'; // green
    case 'Offer':
      return '#2196f3'; // blue
    case 'Rejected':
      return '#f44336'; // red
    default:
      return '#777'; // grey
  }
};

export default JobApplications;
