import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components';
import { AuthContext } from '../contexts/AuthContext';
import atsfsIcon from '../assets/atsfs.png';
import { FiSun, FiMoon } from 'react-icons/fi'; // Import sun and moon icons

// Define light and dark themes
const lightTheme = {
    background: '#ebebeb',
    text: '#2f2f2f',
    hover: '#dedede',
    border: '#ccc',
};

const darkTheme = {
    background: '#2f2f2f',
    text: '#ebebeb',
    hover: '#333333',
    border: '#444',
};

const GlobalStyle = createGlobalStyle`
    body {
        background-color: ${({ theme }) => theme.background};
        color: ${({ theme }) => theme.text};
        transition: background-color 0.3s ease, color 0.3s ease;
    }
`;

const SidebarContainer = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 280px;
    height: 100vh;
    background-color: ${({ theme }) => theme.background};
    padding: 20px;
    color: ${({ theme }) => theme.text};
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    border-right: 1px solid ${({ theme }) => theme.border};
    z-index: 1000;
`;

const SidebarContent = styled.div`
    flex-grow: 1;
`;

const SidebarItem = styled.div`
    margin: 10px 0;
    cursor: pointer;
    padding: 10px;
    border-radius: 5px;
    display: flex;
    align-items: center;

    &:hover {
        background-color: ${({ theme }) => theme.hover};
    }

    & > .material-icons {
        margin-right: 10px;
    }
`;

const AppName = styled.div`
    font-size: 28px;
    font-weight: bold;
    text-align: center;
    margin-bottom: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const DarkModeButton = styled.button`
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    cursor: pointer;
    color: ${({ theme }) => theme.text};
    font-size: 24px;

    &:hover {
        color: ${({ theme }) => theme.hover};
    }
`;

const SidebarFooter = styled.div`
  margin-top: auto;
`;

const Sidebar = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { isLoggedIn, user, logout } = useContext(AuthContext);
    const [isDarkMode, setIsDarkMode] = useState(false); // Track dark mode state
    const navigate = useNavigate();

    const toggleDarkMode = () => {
        setIsDarkMode(!isDarkMode);
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        // Use the existing state instead of trying to set it here
    }, []);

    return (
        <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
            <GlobalStyle />
            <SidebarContainer>
                <SidebarContent>
                    <AppName>
                        <img src={atsfsIcon} alt="Icon" style={{ marginRight: '10px', width: '50px' }} />
                        ATSFS
                    </AppName>
                    <SidebarItem onClick={() => navigate('/')}>
                        <span className="material-icons">home</span>
                        Home
                    </SidebarItem>

                    {isLoggedIn && (
                        <>
                            <SidebarItem onClick={() => navigate('/job-applications')}>
                                <span className="material-icons">work</span>
                                Applications
                            </SidebarItem>
                            <SidebarItem onClick={() => setIsModalOpen(true)}>
                                <span className="material-icons">add</span>
                                Add Job Application
                            </SidebarItem>
                            <SidebarItem onClick={() => navigate('/charts')}>
                                <span className="material-icons">bar_chart</span>
                                Charts
                            </SidebarItem>
                            {/* Other Sidebar Items */}
                        </>
                    )}
                </SidebarContent>

                {/* Dark mode toggle button */}
                <DarkModeButton onClick={toggleDarkMode}>
                    {isDarkMode ? <FiSun /> : <FiMoon />}
                </DarkModeButton>

                <SidebarFooter>
                    {/* Bottom Profile Section */}
                    {isLoggedIn && <div>Logged in as <strong>{user}</strong></div>}
                    <SidebarItem onClick={isLoggedIn ? logout : () => navigate('/login')}>
                        <span className="material-icons">{isLoggedIn ? 'logout' : 'login'}</span>
                        {isLoggedIn ? 'Logout' : 'Login'}
                    </SidebarItem>
                </SidebarFooter>
            </SidebarContainer>
        </ThemeProvider>
    );
};

export default Sidebar;
