import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../contexts/AuthContext';
import { ResponsiveContainer } from 'recharts';
import styled from 'styled-components';

const REACT_APP_BACKEND_URL = process.env.REACT_APP_BACKEND_URL+'/api';

// Styled Components
const Container = styled.div`
  padding: 40px;
  margin-left: 240px; /* Adjust this according to your sidebar width */
  margin-top: 20px;
  box-sizing: border-box;
`;

const Header = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const ChartWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Charts = () => {
    const [data, setData] = useState([]);
    const { isLoggedIn } = useContext(AuthContext);

    useEffect(() => {
        const fetchData = async () => {
            if (isLoggedIn) {
                try {
                    const response = await axios.get(`${REACT_APP_BACKEND_URL}/job-applications/stats`, {
                        headers: {
                            Authorization: `Bearer ${localStorage.getItem('token')}`,
                        },
                    });
                    const applicationsByDay = response.data.applicationsByDay;
                    const chartData = Object.keys(applicationsByDay).map(date => ({
                        date: date,
                        count: applicationsByDay[date],
                    }));
                    setData(chartData);
                } catch (error) {
                    console.error('Error fetching job application stats:', error);
                }
            }
        };
        fetchData();
    }, [isLoggedIn]);

    return (
        <Container>
            <Header>Job Applications by Day</Header>
            <ChartWrapper>
                <ResponsiveContainer width="100%" height={300}>
                    {/* Insert your chart component here */}
                </ResponsiveContainer>
            </ChartWrapper>
        </Container>
    );
};

export default Charts;
