import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [sortColumn, setSortColumn] = useState('companyName');
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/job-applications', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobApplications(response.data);
    };

    fetchData();
  }, []);

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const handleDelete = async (id, e) => {
    // Prevent the click from bubbling up and triggering the modal
    e.stopPropagation();
    const token = localStorage.getItem('token');
    await axios.delete(`http://localhost:8080/api/job-applications/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setJobApplications(jobApplications.filter((app) => app.id !== id));
  };

  const handleRowClick = (app) => {
    setSelectedApplication(app);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedApplication(null);
  };

  const sortedApplications = [...jobApplications].sort((a, b) => {
    if (a[sortColumn] < b[sortColumn]) return sortOrder === 'asc' ? -1 : 1;
    if (a[sortColumn] > b[sortColumn]) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const filteredApplications = sortedApplications.filter((app) =>
    Object.values(app).some((value) => value.toString().toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Job Applications</h2>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={styles.searchInput}
      />
      <table style={styles.table}>
        <thead>
          <tr>
            {['Company', 'Title', 'Status', 'Application Date', 'Response Date', 'Platform'].map((column) => (
              <th key={column} onClick={() => handleSort(column)} style={styles.tableHeader}>
                {column.charAt(0).toUpperCase() + column.slice(1)}
                {sortColumn === column && (
                  <span className="material-icons">{sortOrder === 'asc' ? 'arrow_upward' : 'arrow_downward'}</span>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredApplications.map((app) => (
            <tr key={app.id} onClick={() => handleRowClick(app)} style={styles.tableRow}>
              <td style={styles.tableCell}>
                {app.companyName}
                <button
                  onClick={(e) => handleDelete(app.id, e)}
                  style={styles.deleteButton}
                >
                  Delete
                </button>
              </td>
              <td style={styles.tableCell}>{app.jobTitle}</td>
              <td style={{ ...styles.tableCell, color: getStatusColor(app.status) }}>{app.status}</td>
              <td style={styles.tableCell}>{new Date(app.applicationDate).toLocaleDateString()}</td>
              <td style={styles.tableCell}>{new Date(app.responseDate).toLocaleDateString()}</td>
              <td style={styles.tableCell}>{app.platform}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for editing job application */}
      <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal}>
        {selectedApplication && (
          <div>
            <h2>Edit Job Application</h2>
            <form>
              <label>Company Name</label>
              <input type="text" defaultValue={selectedApplication.companyName} />
              <label>Job Title</label>
              <input type="text" defaultValue={selectedApplication.jobTitle} />
              {/* Add other form fields as necessary */}
              <button type="button" onClick={handleCloseModal}>Close</button>
            </form>
          </div>
        )}
      </Modal>
    </div>
  );
};

const getStatusColor = (status) => {
  switch (status) {
    case 'Applied':
      return 'blue';
    case 'Interview':
      return 'orange';
    case 'Rejected':
      return 'red';
    case 'Accepted':
      return 'green';
    default:
      return 'black';
  }
};

// CSS-in-JS styles
const styles = {
  container: {
    padding: '20px',
  },
  header: {
    marginBottom: '20px',
  },
  searchInput: {
    padding: '10px',
    marginBottom: '20px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    width: '100%',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  tableHeader: {
    padding: '10px',
    textAlign: 'left',
    border: '1px solid #ddd',
    cursor: 'pointer',
    backgroundColor: '#f2f2f2',
  },
  tableRow: {
    '&:nth-child(even)': {
      backgroundColor: '#f9f9f9',
    },
    '&:hover': {
      backgroundColor: '#f1f1f1',
    },
  },
  tableCell: {
    padding: '10px',
    border: '1px solid #ddd',
    position: 'relative',
  },
  deleteButton: {
    marginLeft: '10px',
    padding: '5px 10px',
    backgroundColor: 'red',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default JobApplications;
