import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../contexts/AuthContext';
import { ResponsiveContainer } from 'recharts';
import styled from 'styled-components';
import { PieChart, Pie, Cell, Tooltip, Legend } from 'recharts';

const REACT_APP_BACKEND_URL = process.env.REACT_APP_BACKEND_URL+'/api';

// Styled Components
const Container = styled.div`
    display: flex;
    justify-content: center; /* Center the container horizontally */
    margin: 20px;
    box-sizing: border-box;
`;

const Header = styled.h2`
    text-align: center;
    margin-bottom: 10px;
`;

const TotalApplications = styled.h3`
  text-align: left;
  margin-bottom: 4px;
  color: black;
`;

const ChartWrapper = styled.div`
    background-color: black;
    padding: 20px;
    border-radius: 20px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2); /* Optional: shadow for depth */
    display: flex;
    justify-content: center;
    align-items: center;
`;

const jobPositionData = (jobApplications) => {
    const positionCount = {};
    jobApplications.forEach(app => {
        const position = app.jobTitle;
        if (positionCount[position]) {
            positionCount[position] += 1;
        } else {
            positionCount[position] = 1;
        }
    });
    return Object.entries(positionCount).map(([position, count]) => ({ name: position, value: count }));
};

const GitHubContributionsChart = ({ applicationsByDay }) => {
    const totalDays = 365;
    const days = Array.from({ length: totalDays }, (_, index) => {
        const date = new Date();
        date.setDate(date.getDate() - index);
        return date.toISOString().split('T')[0]; // Format YYYY-MM-DD
    }).reverse();

    return (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(53, 20px)', gap: '4px' }}>
            {days.map((day) => {
                const applications = applicationsByDay[day] || 0;
                const color = applications > 0 ? `rgba(57, 211, 83, ${Math.min(applications / 3, 1)})` : 'rgba(22, 28, 34, 1)'; // Adjust the divisor for color intensity
                return (
                    <div
                        key={day}
                        style={{
                            width: '20px',
                            height: '20px',
                            backgroundColor: color,
                            //border: '1px solid #ddd',
                            borderRadius: '4px',
                            position: 'relative', // Position for the label
                        }}
                        title={`${applications} Applications on ${new Date(day).toLocaleDateString()}`}
                    >
                        {/* Display day of the month */}
                        <span style={{
                            position: 'absolute',
                            bottom: '-18px',
                            left: '50%',
                            transform: 'translateX(-50%)',
                            fontSize: '10px',
                            color: '#fff',
                        }}>
              {new Date(day).getDate()}
            </span>
                        {/* Display month name on the first box of each week */}
                        {day.endsWith('01') && (
                            <span style={{
                                position: 'absolute',
                                top: '-15px',
                                left: '50%',
                                transform: 'translateX(-50%)',
                                fontSize: '10px',
                                color: '#fff', // Change color for visibility
                            }}>
                {new Intl.DateTimeFormat('en-US', { month: 'short' }).format(new Date(day))}
              </span>
                        )}
                    </div>
                );
            })}
        </div>
    );
};

const pieChart = (data) => {
    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FF0000', '#FF1493', '#00FFFF', '#FF4500'];

    return (
        <PieChart width={400} height={400}>
            <Pie
                data={data}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
            >
                {data.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
            </Pie>
            <Tooltip />
            <Legend />
        </PieChart>
    );
};


const Charts = () => {
    const [applicationsByDay, setApplicationsByDay] = useState({});
    const [jobPositionsData, setJobPositionsData] = useState([]);
    const { isLoggedIn } = useContext(AuthContext);

    useEffect(() => {
        const fetchData = async () => {
            if (isLoggedIn) {
                try {
                    const response = await axios.get(`${REACT_APP_BACKEND_URL}/job-applications/stats`, {
                        headers: {
                            Authorization: `Bearer ${localStorage.getItem('token')}`,
                        },
                    });
                    setApplicationsByDay(response.data.applicationsByDay);

                    // Prepare job positions data
                    const jobPositions = prepareJobPositionData(response.data.jobApplications); // Assuming jobApplications is included in the response
                    setJobPositionsData(jobPositions);
                } catch (error) {
                    console.error('Error fetching job application stats:', error);
                }
            }
        };

        fetchData();
    }, [isLoggedIn]);

    // Calculate total applications
    const totalApplications = Object.values(applicationsByDay).reduce((total, count) => total + count, 0);

    return (
        <Container>
            <div>
                <Header>Your Stats</Header>
                <TotalApplications>Total Applications: {totalApplications}</TotalApplications>
                <ChartWrapper>
                    <ResponsiveContainer width="100%" height="100%">
                        <GitHubContributionsChart applicationsByDay={applicationsByDay}/>
                    </ResponsiveContainer>
                </ChartWrapper>
                <Header>Job Positions Distribution</Header>
                <ChartWrapper>
                    <ResponsiveContainer width="100%" height={300}>
                        {pieChart(jobPositionsData)}
                    </ResponsiveContainer>
                </ChartWrapper>
            </div>
        </Container>

    );
};

export default Charts;
