import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../contexts/AuthContext';
import { ResponsiveContainer } from 'recharts';
import styled from 'styled-components';

const REACT_APP_BACKEND_URL = process.env.REACT_APP_BACKEND_URL+'/api';

// Styled Components
const Container = styled.div`
  padding: 40px;
  margin-left: 240px; /* Adjust this according to your sidebar width */
  margin-top: 20px;
  box-sizing: border-box;
`;

const Header = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const ChartWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const GitHubContributionsChart = ({ applicationsByDay }) => {
    const totalDays = 365;
    const days = Array.from({ length: totalDays }, (_, index) => {
        const date = new Date();
        date.setDate(date.getDate() - index);
        return date.toISOString().split('T')[0]; // Format YYYY-MM-DD
    }).reverse();

    return (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(53, 20px)', gap: '2px' }}>
            {days.map((day) => {
                const applications = applicationsByDay[day] || 0;
                const color = applications > 0 ? `rgba(76, 175, 80, ${Math.min(applications / 10, 1)})` : 'rgba(0, 0, 0, 0.1)'; // Adjust the divisor for color intensity
                return (
                    <div
                        key={day}
                        style={{
                            width: '20px',
                            height: '20px',
                            backgroundColor: color,
                            border: '1px solid #ddd',
                        }}
                        title={`Applications: ${applications}`}
                    ></div>
                );
            })}
        </div>
    );
};

const Charts = () => {
    const [applicationsByDay, setApplicationsByDay] = useState({});
    const { isLoggedIn } = useContext(AuthContext); // Use global isLoggedIn

    useEffect(() => {
        const fetchData = async () => {
            if (isLoggedIn) {
                try {
                    const response = await axios.get(`${REACT_APP_BACKEND_URL}/job-applications/stats`, {
                        headers: {
                            Authorization: `Bearer ${localStorage.getItem('token')}`, // Include JWT in the request
                        },
                    });
                    setApplicationsByDay(response.data.applicationsByDay);
                } catch (error) {
                    console.error('Error fetching job application stats:', error);
                }
            }
        };

        fetchData();
    }, [isLoggedIn]);

    return (
        <div>
            <h2>Job Applications by Day</h2>
            <GitHubContributionsChart applicationsByDay={applicationsByDay} />
        </div>
    );
};

export default Charts;
