// src/components/AITools.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
  text-align: center;
`;

const Button = styled.button`
  background-color: #333;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 20px;

  &:hover {
    background-color: #555;
  }
`;

const AITools = () => {
    const [jobApplications, setJobApplications] = useState([]);
    const [interviewQuestions, setInterviewQuestions] = useState([]);

    useEffect(() => {
        const fetchJobApplications = async () => {
            const token = localStorage.getItem("token");
            const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/job-applications`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setJobApplications(response.data);
        };

        fetchJobApplications();
    }, []);

    const handleGenerateQuestions = async () => {
        const token = localStorage.getItem("token");

        if (jobApplications.length === 0) {
            console.error("No job applications available.");
            return;
        }

        const { jobDescription, jobTitle } = jobApplications[0];

        try {
            const response = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/api/llm/generate-interview`, {
                jobDescription,
                jobTitle
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            console.log("Response Data:", response.data); // Log the entire response data

            // Assuming response.data is an object and contains questions in a key called 'questions'
            if (Array.isArray(response.data.questions)) {
                setInterviewQuestions(response.data.questions);
            } else {
                console.error("Expected 'questions' to be an array.");
            }
        } catch (error) {
            console.error("Error generating questions:", error);
        }
    };


    return (
        <Container>
            <h2>Generate Interview Questions</h2>
            <Button onClick={handleGenerateQuestions}>Generate Questions</Button>
            {interviewQuestions.length > 0 && (
                <div>
                    <h3>Generated Questions:</h3>
                    <ul>
                        {interviewQuestions.map((question, index) => (
                            <li key={index}>{question}</li>
                        ))}
                    </ul>
                </div>
            )}
        </Container>
    );
};

export default AITools;
