import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import AddJobApplication from './AddJobApplication';
import { AuthContext } from '../contexts/AuthContext';
import atsfsIcon from '../assets/atsfs.png';

const NavbarContainer = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 60px;
    background-color: #ebebeb;
    padding: 0 20px;
    color: #2f2f2f;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-bottom: 1px solid #ccc;
    z-index: 1000;
`;

const NavbarItems = styled.div`
    display: flex;
    align-items: center;

    @media (max-width: 768px) {
        display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
        position: absolute;
        top: 60px;
        left: 0;
        right: 0;
        background-color: #ebebeb;
        padding: 10px 0;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    }
`;

const NavbarToggle = styled.button`
    display: none;
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: #2f2f2f;

    @media (max-width: 768px) {
        display: block;
    }
`;

const NavbarRight = styled.div`
    display: flex;
    align-items: center;

    @media (max-width: 768px) {
        display: none;
    }
`;

const NavbarItem = styled.div`
    margin: 10px;
    cursor: pointer;
    padding: 10px;
    border-radius: 5px;
    display: flex;

    &:hover {
        background-color: #dedede;
    }
`;

const AppName = styled.div`
    font-size: 24px;
    font-weight: bold;
    display: flex;
    align-items: center;
    cursor: pointer;
`;

const Navbar = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { isLoggedIn, user, logout } = useContext(AuthContext);
    const navigate = useNavigate();
    const [isOpen, setIsOpen] = useState(false);
    const toggleDropdown = () => setIsOpen(!isOpen);

    const handleAddJobClick = () => setIsModalOpen(true);
    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <NavbarContainer>
            <NavbarToggle onClick={toggleDropdown}>
                <span className="material-icons">menu</span>
            </NavbarToggle>

            <NavbarItems isOpen={isOpen}>
                <AppName onClick={() => navigate('/')}>
                    <img src={atsfsIcon} alt="Icon" style={{ marginRight: '10px', width: '40px' }} />
                    ATSFS
                </AppName>
                {isLoggedIn && (
                    <>
                        <NavbarItem onClick={() => navigate('/job-applications')}>Applications</NavbarItem>
                        <NavbarItem onClick={handleAddJobClick}>Add Application</NavbarItem>
                        <NavbarItem onClick={() => navigate('/charts')}>Charts</NavbarItem>
                        <NavbarItem onClick={() => navigate('/ai-tools')}>AI Tools</NavbarItem>
                        <NavbarItem onClick={() => navigate('/resume')}>Resume</NavbarItem>
                        <NavbarItem onClick={() => navigate('/exportData')}>Export Data</NavbarItem>
                    </>
                )}
            </NavbarItems>

            <NavbarRight>
                {isLoggedIn && <div>Logged in as <strong>{user}</strong></div>}
                <NavbarItem onClick={isLoggedIn ? handleLogout : () => navigate('/login')}>
                    {isLoggedIn ? 'Logout' : 'Login'}
                </NavbarItem>
            </NavbarRight>

            <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
        </NavbarContainer>
    );
};

export default Navbar;
