import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filterText, setFilterText] = useState('');
  const [sortColumn, setSortColumn] = useState('companyName');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/job-applications', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobApplications(response.data);
    };

    fetchData();
  }, []);

  const filteredApplications = jobApplications.filter(app =>
    Object.values(app).some(value =>
      value.toString().toLowerCase().includes(filterText.toLowerCase())
    )
  );

  const sortedApplications = filteredApplications.sort((a, b) => {
    const aValue = a[sortColumn];
    const bValue = b[sortColumn];

    if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
    if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending':
        return 'orange';
      case 'Accepted':
        return 'green';
      case 'Rejected':
        return 'red';
      default:
        return 'black';
    }
  };

  return (
    <div>
      <h2>Job Applications</h2>
      <input
        type="text"
        placeholder="Search..."
        value={filterText}
        onChange={e => setFilterText(e.target.value)}
        style={{ marginBottom: '20px', padding: '10px', width: '100%' }}
      />
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th onClick={() => handleSort('companyName')} style={{ cursor: 'pointer' }}>Company Name</th>
            <th onClick={() => handleSort('jobTitle')} style={{ cursor: 'pointer' }}>Job Title</th>
            <th onClick={() => handleSort('status')} style={{ cursor: 'pointer' }}>Status</th>
            <th onClick={() => handleSort('applicationDate')} style={{ cursor: 'pointer' }}>Application Date</th>
            <th onClick={() => handleSort('responseDate')} style={{ cursor: 'pointer' }}>Response Date</th>
            <th onClick={() => handleSort('platform')} style={{ cursor: 'pointer' }}>Platform</th>
          </tr>
        </thead>
        <tbody>
          {sortedApplications.map(app => (
            <tr key={app.id}>
              <td>{app.companyName}</td>
              <td>{app.jobTitle}</td>
              <td style={{ color: getStatusColor(app.status) }}>{app.status}</td>
              <td>{app.applicationDate}</td>
              <td>{app.responseDate}</td>
              <td>{app.platform}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default JobApplications;
