import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        font-family: 'Arial', sans-serif;
        background-color: #ffffff;
        color: #0d0d0d;
    }

    .modal {
        top: 50%; /* Remove quotes */
        left: 50%; /* Remove quotes */
        right: auto;
        bottom: auto;
        margin-right: -50%; /* Remove quotes and use kebab-case */
        transform: translate(-50%, -50%); /* Remove quotes */
        width: 500px; /* Remove quotes */
        padding: 20px; /* Remove quotes */
        border-radius: 10px; /* Remove quotes and use kebab-case */
        background-color: #f5f5f5; /* Remove quotes */
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); /* Remove quotes and use kebab-case */
    }

    .modal-overlay {
        background-color: rgba(0, 0, 0, 0.7); /* Remove quotes */
    }

    button, input {
        border-radius: 5px;
    }
`;

export default GlobalStyle;
