import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TableSortLabel, TextField } from '@mui/material';

const JobApplications = () => {
  const [jobApplications, setJobApplications] = useState([]);
  const [filterText, setFilterText] = useState('');
  const [sortColumn, setSortColumn] = useState('companyName');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/job-applications', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobApplications(response.data);
    };

    fetchData();
  }, []);

  const filteredApplications = jobApplications.filter(app =>
    Object.values(app).some(value =>
      value.toString().toLowerCase().includes(filterText.toLowerCase())
    )
  );

  const sortedApplications = filteredApplications.sort((a, b) => {
    const aValue = a[sortColumn];
    const bValue = b[sortColumn];

    if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
    if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending':
        return 'orange';
      case 'Accepted':
        return 'green';
      case 'Rejected':
        return 'red';
      default:
        return 'black';
    }
  };

  return (
    <div>
      <h2>Job Applications</h2>
      <TextField
        label="Search..."
        variant="outlined"
        fullWidth
        margin="normal"
        value={filterText}
        onChange={e => setFilterText(e.target.value)}
      />
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              {['companyName', 'jobTitle', 'status', 'applicationDate', 'responseDate', 'platform'].map((column) => (
                <TableCell key={column}>
                  <TableSortLabel
                    active={sortColumn === column}
                    direction={sortColumn === column ? sortOrder : 'asc'}
                    onClick={() => handleSort(column)}
                  >
                    {column.charAt(0).toUpperCase() + column.slice(1)}
                  </TableSortLabel>
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {sortedApplications.map(app => (
              <TableRow key={app.id}>
                <TableCell>{app.companyName}</TableCell>
                <TableCell>{app.jobTitle}</TableCell>
                <TableCell style={{ color: getStatusColor(app.status) }}>{app.status}</TableCell>
                <TableCell>{app.applicationDate}</TableCell>
                <TableCell>{app.responseDate}</TableCell>
                <TableCell>{app.platform}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default JobApplications;
