import React from 'react';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
    position: relative;
    padding: 20px;
    background-color: #ffffff;
    color: #333;
    height: auto;
    overflow: hidden; /* To contain absolutely positioned elements */
`;

const Title = styled.h2`
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 40px;
    position: relative;
    z-index: 1; /* Ensure title is above other elements */
`;

const Section = styled.div`
    position: absolute;
    padding: 20px;
    background-color: rgba(245, 245, 245, 0.8); /* Transparent background */
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    width: 280px; /* Fixed width for all sections */
`;

const Illustration = styled.img`
    width: 100%;
    height: auto;
    margin: 10px 0;
`;

const Description = styled.p`
    font-size: 1.2rem;
    text-align: center;
    line-height: 1.6;
`;

const Button = styled.button`
    margin-top: 10px;
    padding: 10px 20px;
    font-size: 1rem;
    color: #fff;
    background-color: #333;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s;

    &:hover {
        background-color: #555;
    }
`;

// Main Home component
const Home = () => {
    return (
        <Container>
            <Title>Job Application Tracker</Title>

            {/* Randomly positioned sections */}
            <Section style={{ top: '10%', left: '5%' }}>
                <Illustration src="https://via.placeholder.com/200x150.png?text=Clock" alt="Clock Illustration" />
                <Description>
                    Keep track of your time effectively with our integrated clock feature.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section style={{ top: '25%', left: '50%', transform: 'translate(-50%, -50%)' }}>
                <Illustration src="https://via.placeholder.com/200x150.png?text=Target" alt="Target Illustration" />
                <Description>
                    Set and achieve your job application goals with targeted tracking.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section style={{ top: '50%', left: '10%' }}>
                <Illustration src="https://via.placeholder.com/200x150.png?text=Commenting" alt="Commenting Illustration" />
                <Description>
                    Comment and collaborate with others during your job search.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section style={{ top: '65%', left: '45%' }}>
                <Illustration src="https://via.placeholder.com/200x150.png?text=Giving Rating" alt="Giving Rating Illustration" />
                <Description>
                    Rate your applications and interviews to evaluate your experiences.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section style={{ top: '80%', left: '20%' }}>
                <Illustration src="https://via.placeholder.com/200x150.png?text=Time Management" alt="Time Management Illustration" />
                <Description>
                    Master your time management skills with reminders tailored to your needs.
                </Description>
                <Button>Learn More</Button>
            </Section>
        </Container>
    );
};

export default Home;
