import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import AddJobApplication from './AddJobApplication';

const SidebarContainer = styled.div`
    position: fixed;
    top: 0;
    left: ${(props) => (props.isHidden ? '-280px' : '0')}; /* Toggle visibility with slide effect */
    width: 280px;
    height: 98vh;
    background-color: #f5f5f5;
    padding: 20px;
    color: black;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    border-radius: 0 0.675rem 0.675rem 0;
    box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    transition: left 0.3s ease; /* Smooth transition for show/hide */
`;

const HamburgerIcon = styled.div`
    position: fixed;
    top: 10px;
    left: 10px;
    font-size: 30px;
    cursor: pointer;
    z-index: 1100;
    background-color: #f5f5f5;
    padding: 10px;
    border-radius: 50%;
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.1);
`;

const SidebarItem = styled.div`
    margin: 20px 0;
    cursor: pointer;
    padding: 10px;
    border-radius: 5px;
    display: flex;
    //align-items: center;

    &:hover {
        background-color: #f2f3f4;
    }

    & > .material-icons {
        margin-right: 10px;
    }
`;

const ProfileContainer = styled.div`
    text-align: center;
    color: black;
    margin-bottom: 30px;
`;

const ProfileButton = styled.button`
    background: none;
    border: none;
    color: black;
    cursor: pointer;
    padding: 10px;
    border-radius: 5px;

    &:hover {
        color: #333;
    }
`;

const Sidebar = ({ setPage }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isHidden, setIsHidden] = useState(false); // Controls sidebar visibility

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleAddJobClick = () => {
        setIsModalOpen(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        setPage('login');
    };

    const toggleSidebar = () => {
        setIsHidden(!isHidden); // Toggles sidebar visibility
    };

    return (
        <>
            {/* Hamburger Menu Icon */}
            <HamburgerIcon className="material-icons" onClick={toggleSidebar}>
                menu
            </HamburgerIcon>

            {/* Sidebar */}
            <SidebarContainer isHidden={isHidden}>

                <div>
                    <SidebarItem onClick={() => setPage('home')}>
                        <span className="material-icons">home</span>
                        Home
                    </SidebarItem>


                </div>
                {/* Profile Section */}
                <ProfileContainer>
                    {isLoggedIn ? (
                        <>
                            <h3>User Profile</h3>
                            <SidebarItem onClick={() => setPage('jobApplications')}>
                                <span className="material-icons">work</span>
                                Applications
                            </SidebarItem>
                            <SidebarItem onClick={handleAddJobClick}>
                                <span className="material-icons">add</span>
                                Add Job Application
                            </SidebarItem>
                            <SidebarItem onClick={() => setPage('myResume')}>
                                <span className="material-icons">assignment_ind</span>
                                My Resume
                            </SidebarItem>
                            <SidebarItem onClick={() => setPage('exportData')}>
                                <span className="material-icons">file_download</span>
                                Export My Data
                            </SidebarItem>
                        </>
                    ) : (
                        <h3>Welcome!</h3>
                    )}
                </ProfileContainer>


                {/* Add Job Application Modal */}
                <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}/>

                {/* Bottom Profile Section */}
                <ProfileContainer>
                    {isLoggedIn ? (
                        <ProfileButton onClick={handleLogout}>
                            <span className="material-icons">logout</span>
                            Logout
                        </ProfileButton>
                    ) : (
                        <ProfileButton onClick={() => setPage('Login')}>
                            <span className="material-icons">login</span>
                            Login
                        </ProfileButton>
                    )}
                    <div>{isLoggedIn ? 'Logged in as User' : ''}</div>
                </ProfileContainer>
            </SidebarContainer>
        </>
    );
};

export default Sidebar;
