import React, { useState } from 'react';
import styled from 'styled-components';
import AddJobApplication from './AddJobApplication';

const SidebarContainer = styled.div`
  width: ${({ isVisible }) => (isVisible ? '300px' : '0')}; /* Adjust width based on visibility */
  height: 100vh;
  background-color: #f5f5f5;
  padding: 20px;
  color: black;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border-radius: 0 0.675rem 0.675rem 0;
  transition: width 0.3s ease; /* Smooth transition when expanding/collapsing */
  overflow: hidden;
  position: relative;
`;

const ToggleButton = styled.button`
  position: absolute;
  top: 10px;
  left: ${({ isVisible }) => (isVisible ? '300px' : '10px')}; /* Move the button based on sidebar visibility */
  background: #f5f5f5;
  border: none;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
  transition: left 0.3s ease;
  z-index: 10;

  &:hover {
    background-color: #e0e0e0;
  }

  .material-icons {
    font-size: 24px;
    color: black;
  }
`;

const SidebarItem = styled.div`
  margin: 20px 0;
  cursor: pointer;
  padding: 10px;
  border-radius: 5px;
  display: flex;
  align-items: center;
  opacity: ${({ isVisible }) => (isVisible ? '1' : '0')}; /* Fade out when not visible */

  &:hover {
    background-color: #f2f3f4;
  }

  & > .material-icons {
    margin-right: 10px;
  }
`;

const ProfileContainer = styled.div`
  margin-top: auto;
  text-align: center;
  color: #aaa;
`;

const ProfileButton = styled.button`
  background: none;
  border: none;
  color: #aaa;
  cursor: pointer;
  padding: 10px;
  border-radius: 5px;

  &:hover {
    color: black;
  }
`;

const Sidebar = ({ setPage }) => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(true); // Manage visibility
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddJobClick = () => {
    setIsModalOpen(true);
  };

  const toggleSidebarVisibility = () => {
    setIsSidebarVisible((prev) => !prev);
  };

  return (
    <>
      {/* Toggle button at the top */}
      <ToggleButton isVisible={isSidebarVisible} onClick={toggleSidebarVisibility}>
        <span className="material-icons">menu</span>
      </ToggleButton>

      {/* Sidebar */}
      <SidebarContainer isVisible={isSidebarVisible}>
        <div>
          <SidebarItem isVisible={isSidebarVisible} onClick={() => setPage('home')}>
            <span className="material-icons">home</span>
            Home
          </SidebarItem>
          <SidebarItem isVisible={isSidebarVisible} onClick={() => setPage('jobApplications')}>
            <span className="material-icons">work</span>
            Applications
          </SidebarItem>
          <SidebarItem isVisible={isSidebarVisible} onClick={handleAddJobClick}>
            <span className="material-icons">add</span>
            Add Job Application
          </SidebarItem>
        </div>

        <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />

        <ProfileContainer>
          <ProfileButton onClick={() => setPage('profile')}>
            <span className="material-icons">person</span>
            Profile
          </ProfileButton>
          <div>info</div>
        </ProfileContainer>
      </SidebarContainer>
    </>
  );
};

export default Sidebar;
