import React from 'react';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 20px;
    background-color: #fff;
    color: #333;
    text-align: center;
    height: 100vh;
`;

const Title = styled.h2`
    font-size: 2.5rem;
    margin-bottom: 20px;
`;

const Section = styled.div`
    margin: 40px 0;
`;

const Illustration = styled.img`
    width: 60%;
    max-width: 300px; /* Adjust based on your design */
    height: auto;
    margin: 10px 0;
`;

const Description = styled.p`
    font-size: 1.2rem;
    max-width: 600px;
    line-height: 1.6;
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 1rem;
  color: #fff;
  background-color: #333;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }
`;

// Main Home component
const Home = () => {
    return (
        <Container>
            <Title>Job Application Tracker</Title>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Clock" alt="Clock Illustration" />
                <Description>
                    Keep track of your time effectively with our integrated clock feature. Manage your job search without the pressure of deadlines.
                </Description>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Target" alt="Target Illustration" />
                <Description>
                    Set and achieve your job application goals with targeted tracking. Focus on your desired roles and stay organized.
                </Description>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Commenting" alt="Commenting Illustration" />
                <Description>
                    Comment and collaborate with others during your job search. Share insights and tips within the platform for better opportunities.
                </Description>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Giving Rating" alt="Giving Rating Illustration" />
                <Description>
                    Rate your applications and interviews to evaluate your experiences. Keep notes and reflections to improve your future applications.
                </Description>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Time Management" alt="Time Management Illustration" />
                <Description>
                    Master your time management skills with reminders and schedules tailored to your job search needs.
                </Description>
            </Section>

            <Button>Get Started</Button>
        </Container>
    );
};

export default Home;
