import React, { useState } from 'react';
import styled from 'styled-components';
import AddJobApplication from './AddJobApplication';

const SidebarContainer = styled.div`
  width: ${(props) => (props.isCollapsed ? '80px' : '300px')}; /* Adjust width based on collapse state */
  height: 100vh;
  background-color: #f5f5f5;
  padding: 20px;
  color: black;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border-radius: 0 0.675rem 0.675rem 0;
  transition: width 0.3s; /* Smooth transition for collapsing */
`;

const SidebarItem = styled.div`
  margin: 20px 0;
  cursor: pointer;
  padding: 10px;
  border-radius: 5px;
  display: flex;
  align-items: center;

  &:hover {
    background-color: #f2f3f4;
  }

  & > .material-icons {
    margin-right: ${(props) => (props.isCollapsed ? '0' : '10px')}; /* Space between icon and text */
    transition: margin-right 0.3s; /* Smooth transition for margin */
  }

  & > span {
    display: ${(props) => (props.isCollapsed ? 'none' : 'inline')}; /* Hide text when collapsed */
  }
`;

const CollapseButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  padding: 10px;
  display: flex;
  align-items: center;
  margin-bottom: 20px;

  &:hover {
    background-color: #f2f3f4;
    border-radius: 5px;
  }
`;

const ProfileContainer = styled.div`
  margin-top: auto; /* Push to bottom */
  text-align: center;
  color: #aaa; /* Light text color */
`;

const ProfileButton = styled.button`
  background: none;
  border: none;
  color: #aaa;
  cursor: pointer;
  padding: 10px;
  border-radius: 5px;

  &:hover {
    color: black;
  }
`;

const Sidebar = ({ setPage }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false); // State for sidebar collapse

  const handleAddJobClick = () => {
    setIsModalOpen(true);
  };

  const toggleSidebar = () => {
    setIsCollapsed((prev) => !prev); // Toggle the collapse state
  };

  return (
    <SidebarContainer isCollapsed={isCollapsed}>
      <CollapseButton onClick={toggleSidebar}>
        <span className="material-icons">{isCollapsed ? 'chevron_right' : 'chevron_left'}</span>
      </CollapseButton>

      <div>
        <SidebarItem isCollapsed={isCollapsed} onClick={() => setPage('home')}>
          <span className="material-icons">home</span>
          <span>Home</span>
        </SidebarItem>
        <SidebarItem isCollapsed={isCollapsed} onClick={() => setPage('jobApplications')}>
          <span className="material-icons">work</span>
          <span>Applications</span>
        </SidebarItem>
        <SidebarItem isCollapsed={isCollapsed} onClick={handleAddJobClick}>
          <span className="material-icons">add</span>
          <span>Add Job Application</span>
        </SidebarItem>
      </div>
      <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />

      <ProfileContainer>
        <ProfileButton onClick={() => setPage('profile')}>
          <span className="material-icons">person</span>
          <span>Profile</span>
        </ProfileButton>
        <div>info</div>
      </ProfileContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
