import React, { useState } from 'react';
import axios from 'axios';

const AddJobApplication = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    jobTitle: '',
    status: '',
    applicationDate: '',
    responseDate: '',
    platform: '',
    jobUrl: '',
    description: '',
    comments: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    await axios.post("http://localhost:8080/addjobapplication", formData, {
      headers: { Authorization: `Bearer ${token}` }
    });
  };

  return (
    <div style={{ color: 'white', backgroundColor: 'black', padding: '20px' }}>
      <h2>Add Job Application</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="companyName" placeholder="Company Name" onChange={handleChange} />
        <input type="text" name="jobTitle" placeholder="Job Title" onChange={handleChange} />
        <button type="submit" style={{ backgroundColor: 'white', color: 'black' }}>Add</button>
      </form>
    </div>
  );
};

export default AddJobApplication;
