import React from 'react';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #fff;
  color: #333;
  text-align: center;
  padding: 20px;
`;

const Title = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const Illustration = styled.img`
  width: 60%;
  max-width: 400px; /* Adjust based on your design */
  height: auto;
  margin-bottom: 20px;
`;

const Description = styled.p`
  font-size: 1.2rem;
  max-width: 600px;
  line-height: 1.6;
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 1rem;
  color: #fff;
  background-color: #333;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }
`;

// Main Home component
const Home = () => {
    return (
        <Container>
            <Title>Job Application Tracker</Title>
            <Illustration src="https://via.placeholder.com/400x300.png?text=Illustration" alt="Job Tracker Illustration" />
            <Description>
                Keep track of your job applications seamlessly. Organize your job search, manage your applications, and never miss an opportunity!
            </Description>
            <Button>Get Started</Button>
        </Container>
    );
};

export default Home;
