import React from 'react';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 20px; /* Space between grid items */
    padding: 20px;
    background-color: #ffffff;
    color: #333;
    height: auto;
`;

const Title = styled.h2`
    grid-column: span 2; /* Centered title across two columns */
    font-size: 2.5rem;
    text-align: center;
    margin-bottom: 20px;
`;
const Head = styled.h2`
    grid-column: span 2; /* Centered title across two columns */
    font-size: 2.5rem;
    text-align: center;
    margin-bottom: 20px;
`;

const Section = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    padding: 20px;
    background-color: #f9f9f9;
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
`;

const Illustration = styled.img`
    width: 100%;
    height: auto;
    max-width: 200px; /* Adjust based on your design */
    margin: 10px 0;
`;

const Description = styled.p`
    font-size: 1.2rem;
    text-align: center;
    line-height: 1.6;
`;

const Button = styled.button`
    margin-top: 10px;
    padding: 10px 20px;
    font-size: 1rem;
    color: #fff;
    background-color: #333;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s;

    &:hover {
        background-color: #555;
    }
`;

// Main Home component
const Home = () => {
    return (
        <Container>
            <Title>Job Application Tracker</Title>
            <Head>Track & Organize Your Job Search</Head>
            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Clock" alt="Clock Illustration" />
                <Description>
                    Keep track of your time effectively with our integrated clock feature. Manage your job search without the pressure of deadlines.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Target" alt="Target Illustration" />
                <Description>
                    Set and achieve your job application goals with targeted tracking. Focus on your desired roles and stay organized.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Commenting" alt="Commenting Illustration" />
                <Description>
                    Comment and collaborate with others during your job search. Share insights and tips within the platform for better opportunities.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Giving Rating" alt="Giving Rating Illustration" />
                <Description>
                    Rate your applications and interviews to evaluate your experiences. Keep notes and reflections to improve your future applications.
                </Description>
                <Button>Learn More</Button>
            </Section>

            <Section>
                <Illustration src="https://via.placeholder.com/300x200.png?text=Time Management" alt="Time Management Illustration" />
                <Description>
                    Master your time management skills with reminders and schedules tailored to your job search needs.
                </Description>
                <Button>Learn More</Button>
            </Section>
        </Container>
    );
};

export default Home;
