import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AddJobApplication from './AddJobApplication';
import { AuthContext } from '../contexts/AuthContext';
import atsfsIcon from '../assets/atsfs.png';
import '../styles.css'; // Import your global CSS file

const Sidebar = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { isLoggedIn, setIsLoggedIn } = useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleAddJobClick = () => {
        setIsModalOpen(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        navigate('/login');
    };

    return (
        <div className="sidebar-container">
            <div>
                <div className="app-name">
                    <img src={atsfsIcon} alt="Icon" style={{ marginRight: '10px', width: '50px' }} />
                    ATSFS
                </div>
                <div className="sidebar-item" onClick={() => navigate('/')}>
                    <span className="material-icons">home</span>
                    Home
                </div>
                {/* Other items here */}
            </div>
            <AddJobApplication isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
            <div className="profile-container">
                <hr className="horizontal-line" />
                <div className="sidebar-item" onClick={isLoggedIn ? handleLogout : () => navigate('/login')}>
                    <span className="material-icons">{isLoggedIn ? 'logout' : 'login'}</span>
                    {isLoggedIn ? 'Logout' : 'Login'}
                </div>
            </div>
        </div>
    );
};

export default Sidebar;
